import React, { useState } from 'react';
import './NewGame.css';
import HistoryStack from './HistoryStack';
import Tips from './Tips';

function Cell(props) {
	let left = props.size * props.x + 1;
	let top = props.size * props.y + 1;
	let size = props.size - 1;
	const [color] = useState(props.color);
	return (
		<div
			className="Cell"
			style={{
				left,
				top,
				width: size,
				height: size,
				backgroundColor: `rgb(${color.r}, ${color.g}, ${color.b})`,
			}}
		/>
	);
}

export default function Board(props) {
	// references
	let boardRef = React.createRef();
	const [cells, setCells] = useState([]);
	const [board] = useState(makeEmptyBoard(props.rows, props.cols));

	return (
		<div
			className="Board"
			style={{
				width: props.size * props.cols,
				height: props.size * props.rows,
				backgroundSize: `${props.size}px ${props.size}px`,
			}}
			ref={boardRef}
			onClick={handleClick}
		>
			{props.cells.map((cell) => (
				<Cell
					x={cell.x}
					y={cell.y}
					color={cell.color}
					size={props.size}
					key={`${cell.y}, ${cell.x}`}
				/>
			))}
		</div>
	);

	/**
	 *
	 * @param {MouseEvent} event
	 */
	function handleClick(event) {
		const { x, y } = getClickPosition(event);

		if (x >= 0 && x < props.cols && y >= 0 && y <= props.rows)
			board[y][x] = !board[y][x];
		setCells(makeCellsFromBoard(board));
	}

	function makeCellsFromBoard(board) {
		let cells = [];
		for (let y = 0; y < props.rows; y++) {
			for (let x = 0; x < props.cols; x++) {
				if (board[y][x]) cells.push({ x, y });
			}
		}
		return cells;
	}

	/**
	 *
	 * @param {MouseEvent} event
	 */
	function getClickPosition(event) {
		const rect = boardRef.current.getBoundingClientRect();
		const offX = event.clientX - rect.left;
		const offY = event.clientY - rect.top;
		const x = Math.floor(offX / props.size);
		const y = Math.floor(offY / props.size);
		return {
			x,
			y,
		};
	}
}

function Game(props) {
	const [board] = useState(makeEmptyBoard(props.rows, props.cols));
	const [cells, setCells] = useState([]);

	return (
		<Board
			cells={cells}
			size={props.size}
			rows={props.row}
			cols={props.cols}
			onClick
		/>
	);
}

// utilities
function randomColor() {
	const rc = () => 30 + Math.floor(Math.random() * 200);
	return {
		r: rc(),
		g: rc(),
		b: rc(),
	};
}

function makeEmptyBoard(rows, cols) {
	let results = [];
	for (let y = 0; y < rows; y++) {
		results.push([]);
		for (let x = 0; x < cols; x++) {
			results[y].push(false);
		}
	}
	return results;
}
