import './Tips.css';

export default function Tips(props) {
	return (
		<div
			className="tip-container"
			style={{
				width: props.width ? props.width : '100%',
				textAlign: props.textAlign ? props.textAlign : 'left',
				margin: props.margin ? props.margin : '20px auto',
			}}
		>
			<div>
				<div className="btn-container">
					<button
						className="btn-close"
						onClick={(event) => {
							document.querySelector('.tip-container').remove();
						}}
					>
						X
					</button>
				</div>
				<p className="tip-content">
					Click the board to add or remove a cell. Click "Next" to get the
					next iteration and click "Prev" to go back. You can go back up to
					10 steps. Finally, click "Reset" to simply clear the board and
					start over.
				</p>
			</div>
		</div>
	);
}
