import './App.css';
import Game from './NewGame';

function App() {
	let size = 30;
	let rows = Math.floor((window.innerHeight - 100) / size);
	let cols = Math.floor(window.innerWidth / 30);
	return (
		<div className="App">
			<h1 style={{ marginTop: '20px' }}>Game Of Life</h1>
			<Game size={size} rows={rows} cols={cols} />
		</div>
	);
}

export default App;
