import { useState } from 'react';
import './App.css';
import Game from './NewGame';

function App() {
	let size = 30;
	let [rows, setRows] = useState(Math.floor((window.innerHeight - 100) / size));
	let [cols, setCols] = useState(Math.floor(window.innerWidth / size));
	window.addEventListener('resize', () => {
		this.setRows(Math.floor((window.innerHeight - 100) / size));
		this.setCols(Math.floor(window.innerWidth / size));
	});
	return (
		<div className="App">
			<h1 style={{ marginTop: '20px' }}>Game Of Life</h1>
			<Game size={size} rows={rows} cols={cols} />
		</div>
	);
}

export default App;
