import React, { useState } from 'react';
import './NewGame.css';
import HistoryStack from './HistoryStack';
import Tips from './Tips';

function Cell(props) {
	let left = props.size * props.x + 1;
	let top = props.size * props.y + 1;
	let size = props.size - 1;
	return (
		<div
			className="Cell"
			style={{
				left,
				top,
				width: size,
				height: size,
				backgroundColor: `rgb(${randomColor()}, ${randomColor()}, ${randomColor()})`,
			}}
		/>
	);
}

export default function Board(props) {
	// props.rows, props.cols, props.size
	let boardRef = React.createRef();
	const [cells, setCells] = useState([]);
	const [board] = useState([]);

	return (
		<div
			className="Board"
			style={{
				width: props.size * props.cols,
				height: props.size * props.rows,
				backgroudSize: `${props.size}px ${props.size}px`,
			}}
			ref={boardRef}
			onClick={handleClick}
		>
			{cells.map((cell) => (
				<Cell
					x={cell.x}
					y={cell.y}
					size={props.size}
					key={`${cell.x}, ${cell.y}`}
				/>
			))}
		</div>
	);

	/**
	 *
	 * @param {MouseEvent} event
	 */
	function handleClick(event) {
		const rect = boardRef.current.getBoundingClientRect();
		const doc = document.documentElement;

		const x = event.clientX - (rect.left + window.pageXOffset - doc.clientLeft);
		const y = event.clientY - (rect.top + window.pageYOffset - doc.clientTop);

		if (x >= 0 && x < props.cols && y >= 0 && y <= props.rows)
			board[y][x] = !board[y][x];
		setCells(makeCellsFromBoard(board));
	}

	function makeCellsFromBoard(board) {
		let cells = [];
		for (let y = 0; y < props.rows; y++) {
			for (let x = 0; x < props.cols; x++) {
				if (board[y][x]) cells.push({ x, y });
			}
		}
		return cells;
	}
}

// utilities
function randomColor() {
	return 30 + Math.floor(Math.random() * 200);
}
