import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

function Cell(props) {
	return <button className="cell" onClick={props.onClick}></button>;
}

function Board(props) {
	function renderCell(i) {
		return <Cell onClick={() => props.onClick(i)} key={i} />;
	}

	let rows = [];
	let cols = [];
	for (let i = 0; i < props.rows; i++) {
		rows.push(i);
	}
	for (let i = 0; i < props.cols; i++) {
		cols.push(i);
	}

	return (
		<div style={{ margin: '0px auto', maxWidth: '800px' }}>
			{rows.map((i) => {
				return (
					<div className="board-row" key={`row ${i}`}>
						{cols.map((j) => renderCell(i * props.rows + j))}
					</div>
				);
			})}
		</div>
	);
}

function Game(props) {
	const [history, setHistory] = useState([]);
	const [cells, setCells] = useState({});

	function handleClick(i) {
		// cell i was clicked => we need to filp the status of cells i
		if (cells[i]) {
			cells[i] = !cells[i];
			setCells(cells);
		} else {
			cells[i] = true;
			setCells(cells);
		}
	}
}

ReactDOM.render(
	<React.StrictMode>
		<Board rows={20} cols={30} />
	</React.StrictMode>,
	document.getElementById('root')
);
