import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

function Cell(props) {
	const [clicked, setClicked] = useState(false);
	return (
		<button
			className="cell"
			onClick={() => {
				props.onClick();
				setClicked(!clicked);
			}}
			style={{
				backgroundColor: clicked ? 'black' : 'white',
			}}
		></button>
	);
}

function Board(props) {
	function renderCell(i) {
		return <Cell onClick={() => props.onClick(i)} key={i} />;
	}

	let rows = [];
	let cols = [];
	for (let i = 0; i < props.rows; i++) {
		rows.push(i);
	}
	for (let i = 0; i < props.cols; i++) {
		cols.push(i);
	}

	return (
		<div style={{ margin: '0px auto', maxWidth: '800px' }}>
			{rows.map((i) => {
				return (
					<div className="board-row" key={`row ${i}`}>
						{cols.map((j) => renderCell(i * props.cols + j))}
					</div>
				);
			})}
		</div>
	);
}

function Game(props) {
	const [history, setHistory] = useState([]);
	const [board] = useState({});

	function handleClick(i) {
		console.log(`clicked ${i}`);
		console.log(board[i]);
		// cell i was clicked => we need to filp the status of cells i
		if (board[i]) {
			board[i] = !board[i];
		} else {
			board[i] = true;
		}
		console.log(board[i]);
	}

	return (
		<Board
			rows={props.rows}
			cols={props.cols}
			onClick={(i) => handleClick(i)}
		/>
	);
}

ReactDOM.render(
	<React.StrictMode>
		<Game rows={20} cols={30} />
	</React.StrictMode>,
	document.getElementById('root')
);
